package input.vo;import java.util.ArrayList;public class InputVerbVO {	private String word;	private ArrayList<Integer> emoCount;	private int wordEmo;			public InputVerbVO(String word, ArrayList<Integer> emoCount,			int wordEmo) {		super();		this.word = word;		this.emoCount = emoCount;		this.wordEmo = wordEmo;	}	public InputVerbVO(String word, ArrayList<Integer> emoCount) {		super();		this.word = word;		this.emoCount = emoCount;	}	public String getWord() {		return word;	}	public void setWord(String word) {		this.word = word;	}	public ArrayList<Integer> getEmoCount() {		return emoCount;	}	public void setEmoCount(ArrayList<Integer> emoCount) {		this.emoCount = emoCount;	}	public int getWordEmo() {		return wordEmo;	}	public void setWordEmo(int wordEmo) {		this.wordEmo = wordEmo;	}}