package input.vo;import java.util.ArrayList;public class InputWordVO {			private ArrayList<InputNounVO> nuonList;	private ArrayList<InputVerbVO> verbList;	private int wordEmo;		public InputWordVO(ArrayList<InputNounVO> nuonList,			ArrayList<InputVerbVO> verbList, int wordEmo) {		super();		this.nuonList = nuonList;		this.verbList = verbList;		this.wordEmo = wordEmo;	}		public ArrayList<InputNounVO> getNuonList() {		return nuonList;	}	public void setNuonList(ArrayList<InputNounVO> nuonList) {		this.nuonList = nuonList;	}	public ArrayList<InputVerbVO> getVerbList() {		return verbList;	}	public void setVerbList(ArrayList<InputVerbVO> verbList) {		this.verbList = verbList;	}	public int getWordEmo() {		return wordEmo;	}	public void setWordEmo(int wordEmo) {		this.wordEmo = wordEmo;	}						}